import React, { useContext, useRef } from "react";
import "./Home.css";
import Header from "../Header/Header";
import MangasList from "../MangaList/MangasList";
import { MangaContext } from "../MangaCont";

const Home = () => {
  const filterStudentInput = useRef("");
  const [manga, setManga] = useContext(MangaContext);
  const filterStudentByTitle = () => {
    let query = filterStudentInput.current.value;
    if (query === "") setManga([...listStudentBackup]);
    else {
      let newlistStudent = [...listStudent];
      newlistStudent = newlistStudent.filter((titleStudent) =>
        titleStudent.includes(query)
      );
      setManga([...newlistStudent]);
    }
  };
  return (
    <>
      <div>
        <Header filter={filterStudentByTitle}></Header>
        <main>
          <div className="first-row">
            {manga.map((m) => (
              <MangasList m={m} key={m.id} />
            ))}
          </div>
        </main>
      </div>
    </>
  );
};

export default Home;
